import useFetch from "../../hooks/useFetch";
import "./featuredCanchas.css"

const FeaturedCanchas = () =>{

    const {data,loading} = useFetch("/canchas?featured=true&limit=4");

    return (
        <div className="fc">
          {loading ? (
            "Cargando..."
          ) : (
            <>
              {data.map((C) => (
                <div className="fcItem" key={C._id}>
                  <img src={C.photos[0]} alt="" className="fcImg" />
                <span className="fcName">{C.name}</span>
                <span className="fcCity"> {C.city}</span>
                <span className="fcPrice"> hora ${C.cheapestPrice}</span>
                {C.rating && <div className="fcRating">
                <button>{C.rating}</button>
                <span>Excelente</span>
               </div>}
               </div>
               ))}
            </>
)}
        </div>
    )
}

export default FeaturedCanchas;
